import Card from 'react-bootstrap/Card';
import React, {useState,useEffect} from 'react';
/* import Badge from 'react-bootstrap/Badge'; */

const CarouselCard = (movie) => {
    const [additionalData,setAdditionalData] = useState(null);
    console.log(movie.movie.imdbID);

    useEffect(() => {
        fetch(`http://www.omdbapi.com/?apikey=36043e2&i=${movie.movie.imdbID}`)
          .then(response => response.json())
          .then(data => {
            setAdditionalData(data);
          })
          .catch(error => {
            console.error('Error fetching additional data:', error);
          });
      }, [movie.movie.imdbID]); // Fetch data when the movie ID changes
      
    


      return (
        <div className="card">
          <img src={movie.movie.Poster} className="card-img-top" alt={movie.movie.Title} />
          <div className="card-body">
            <h5 className="card-title">{movie.movie.Title}</h5>
            <p className="card-text">{movie.movie.Year}</p>
            {additionalData && (
              <>
                <p>Additional Information:</p>
                <ul>
                  <li>{additionalData.someProperty}</li>
                  <li>{additionalData.anotherProperty}</li>
                  {/* Include additional properties as needed */}
                </ul>
              </>
            )}
            <a href={`https://www.imdb.com/title/${movie.movie.imdbID}`} className="btn btn-primary">View Details</a>
          </div>
        </div>
      );
    };

export default CarouselCard;
