// src/components/LoginPage.js

import React, {useState} from 'react';
import {useNavigate} from 'react-router-dom'
// import axios from 'axios';
import './styling/LoginPage.css'

const LoginPage = ({ isAuthenticated, setIsAuthenticated }) => {
  
    const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isLoginMode, setIsLoginMode] = useState(true); // Determine if it's login or create user mode

  const handleSubmit = async (event) => {
    event.preventDefault();

    const userData = {
      username: username,
      password: password,
    };

    // Determine the API endpoint based on the mode
    const endpoint = isLoginMode ? '/api/users/login' : '/api/users/create';

    try {
      const response = await fetch(`http://localhost:5000${endpoint}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userData),
      });

      const result = await response.json();
      if (response.ok) {
        console.log('User authenticated successfully:', result);
        setIsAuthenticated(true);
        navigate('/');
      } else {
        console.error('Error authenticating user:', result.message);
      }
    } catch (error) {
      console.error('Error authenticating user:', error);
    }
  };

  const toggleMode = () => {
    setIsLoginMode(!isLoginMode);
  };

  return (
    <div className="login-page">
      <div className={`container ${isLoginMode ? 'login' : 'signup'}`}>
        <div className="toggle-button" onClick={toggleMode}>
          {isLoginMode ? 'Switch to Signup' : 'Switch to Login'}
        </div>
        <form onSubmit={handleSubmit}>
          <h2>{isLoginMode ? 'Login' : 'Create User'}</h2>
          <div className="form-group">
            <label>Username:</label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label>Password:</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit">{isLoginMode ? 'Login' : 'Create User'}</button>
        </form>
      </div>
    </div>
  );
};

export default LoginPage;
