import React, { useEffect, useState } from 'react';
import { Card, Button } from 'react-bootstrap';

const CarouselCard = ({ movie }) => {
  const [additionalInfo, setAdditionalInfo] = useState({});
  const [isExpanded, setIsExpanded] = useState(false);

  useEffect(() => {
    const fetchAdditionalInfo = async () => {
      try {
        const response = await fetch(`http://www.omdbapi.com/?apikey=36043e2&i=${movie.imdbID}`);
        const data = await response.json();
        setAdditionalInfo(data);
      } catch (error) {
        console.error('Error fetching additional info:', error);
      }
    };

    fetchAdditionalInfo();
  }, [movie.imdbID]);

  const posterSrc = movie.Poster && movie.Poster !== 'N/A' ? movie.Poster : 'placeholder-image-url.jpg';

  return (
    <Card className="card">
      <Card.Img variant="top" src={posterSrc} className="card-img-top" alt={movie.Title} />
      <Card.Body className="card-body">
        <Card.Title>{movie.Title}</Card.Title>
        <Card.Subtitle>{movie.Year}</Card.Subtitle>
        <div className={isExpanded ? "card-text-expanded" : "card-text-collapsed"}>
          {additionalInfo.Plot || 'No plot available'}
          {!isExpanded && (
            <button className="expand-button" onClick={() => setIsExpanded(true)}>...</button>
          )}
        </div>
        {isExpanded && (
          <Button className="expand-button" onClick={() => setIsExpanded(false)}>
            Show Less
          </Button>
        )}
        <Button href={`https://www.imdb.com/title/${movie.imdbID}`} className="btn btn-primary mt-2">View Details</Button>
      </Card.Body>
    </Card>
  );
};

export default CarouselCard;
