import React, { useEffect, useState, useRef } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import SearchBar from './components/SearchBar';
import MovieList from './components/MovieList';

const App = () => {
  const [movies, setMovies] = useState([]);
  const [searchValue, setSearchValue] = useState('');
  const [scrollInterval, setScrollInterval] = useState(null);
  const movieListContainerRef = useRef(null);

  // Fetch movies based on search term
  const fetchMovies = async (searchValue) => {
    const url = `http://www.omdbapi.com/?apikey=36043e2&s=${searchValue}`;
    const response = await fetch(url);
    const responseJson = await response.json();

    if (responseJson.Search) {
      setMovies(responseJson.Search);
    }
  };

  useEffect(() => {
    if (searchValue) {
      fetchMovies(searchValue);
    }
  }, [searchValue]);

  useEffect(() => {
    // Function to handle continuous scrolling
    const handleContinuousScroll = () => {
      const container = movieListContainerRef.current;
      if (container && scrollInterval) {
        if (container.scrollLeft === 0 && scrollInterval < 0) {
          clearInterval(scrollInterval);
          setScrollInterval(null);
        } else {
          container.scrollLeft += 2 * scrollInterval;
        }
      }
    };

    // Add event listeners for continuous scrolling
    const handleLeftMouseEnter = () => setScrollInterval(-1);
    const handleRightMouseEnter = () => setScrollInterval(1);
    const handleMouseLeave = () => setScrollInterval(null);

    document.getElementById('left-scroll').addEventListener('mouseenter', handleLeftMouseEnter);
    document.getElementById('right-scroll').addEventListener('mouseenter', handleRightMouseEnter);
    document.addEventListener('mouseleave', handleMouseLeave);

    // Remove event listeners on component unmount
    return () => {
      document.getElementById('left-scroll').removeEventListener('mouseenter', handleLeftMouseEnter);
      document.getElementById('right-scroll').removeEventListener('mouseenter', handleRightMouseEnter);
      document.removeEventListener('mouseleave', handleMouseLeave);
    };
  }, [scrollInterval, movieListContainerRef]);

  return (
    <div className="App">
      <div className="container-fluid movie-app">
        <h1>Movie Search App</h1>
        <SearchBar searchValue={searchValue} setSearchValue={setSearchValue} />
        <div className="movie-list-container" ref={movieListContainerRef}>
          <div className="scroll-arrow left" id="left-scroll">
            {'<'}
          </div>
          <MovieList movies={movies} />
          <div className="scroll-arrow right" id="right-scroll">
            {'>'}
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
