import React, { useRef } from 'react';
import CarouselCard from './CarouselCard';

const MovieList = ({ movies }) => {
  const movieListRef = useRef(null);

  const handleScroll = (direction) => {
    const scrollStep = 200; // Adjust as needed
    const scrollContainer = movieListRef.current;

    if (direction === 'left') {
      scrollContainer.scrollBy({ left: -scrollStep, behavior: 'smooth' });
    } else {
      scrollContainer.scrollBy({ left: scrollStep, behavior: 'smooth' });
    }
  };

  return (
    <div className="movie-list-container">
      <button className="scroll-button scroll-left" onClick={() => handleScroll('left')}>
        &lt;
      </button>
      <div className="movie-list" ref={movieListRef}>
        {movies.map((movie, index) => (
          <div key={index} className="movie-card">
            <CarouselCard movie={movie} />
          </div>
        ))}
      </div>
      <button className="scroll-button scroll-right" onClick={() => handleScroll('right')}>
        &gt;
      </button>
    </div>
  );
};

export default MovieList;
