import logo from './logo.svg';
import './App.css';
import React, { useEffect, useState } from 'react';
import SearchBar from './components/SearchBar';
import CardCarousel from './components/CardCarousel';

const App = () => {
  const [movies, setMovies] = useState([]);
  const [searchValue, setSearchValue] = useState('');

  // Fetch movies based on search term
  const fetchMovies = async (searchValue) => {

    const url= `http://www.omdbapi.com/?apikey=36043e2&s=${searchValue}`;

    const response = await fetch(url);
    const responseJson = await response.json();

    console.log(responseJson);

    if(responseJson.Search){
      setMovies(responseJson.Search);
    }

      /* .then(response => response.json())
      .then(data => {
        if (data.Search) {
          setMovies(data.Search);
          if(data.Search.imbdID){
            fetch(`http://www.omdbapi.com/?apikey=36043e2&i=${data.Search.imbdID}`)
            .then(response => response.json())
            .then(data => movies{

          }
        } else {
          setMovies([]);
        }
        // console.log(data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      }); */
  };

  useEffect(() => {
    fetchMovies(searchValue);
  }, [searchValue]);

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          <code>src/App.js</code>
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
      <div className='container-fluid movie-app'>
        <h1>Movie Search App</h1>
        <div className='row'>
          <SearchBar searchValue={searchValue} setSearchValue={setSearchValue}/>
          <CardCarousel movies={movies} />
        </div>
      </div>
    </div>
  );
}

export default App;
